mapper.select:
SELECT

FROM gen_config_template a

WHERE id =  #{id}

SELECT

FROM gen_config_template a

WHERE a.del_flag = #{DEL_FLAG_NORMAL}

    and template_name like concat('%', #{templateName}, '%')


    and function_author like concat('%', #{functionAuthor}, '%')


    and status = #{status}

SELECT
    a.id
FROM gen_config_template a

WHERE a.del_flag = #{DEL_FLAG_NORMAL}

    and template_name = #{templateName}


    AND id != #{notEqualId}

SELECT max(a.sort)
FROM gen_config_template a
WHERE a.del_flag = #{DEL_FLAG_NORMAL}

mapper.update:
update gen_config_template
template_name = #{templateName}, function_author = #{functionAuthor}, function_author_email = #{functionAuthorEmail}, workspace_path = #{workspacePath}, module_name = #{moduleName}, package_name = #{packageName}, web_workspace_path = #{webWorkspacePath}, template_default = #{templateDefault}, sort = #{sort}, status = #{status}, update_by = #{updateBy}, update_time = #{updateTime}, update_ip = #{updateIp}, remark = #{remark}, del_flag = #{delFlag}, version = version + 1
where id = #{id} and version = #{version}

mapper.delete:
        UPDATE gen_config_template SET
			del_flag = #{DEL_FLAG_DELETE}
		WHERE id = #{id}
