mapper.select:
SELECT

FROM sys_dept t
where t.id = #{id}

SELECT

FROM sys_dept t
where t.del_flag = #{DEL_FLAG_NORMAL}

SELECT
	t.id
FROM sys_dept t
WHERE t.del_flag = #{DEL_FLAG_NORMAL}

	AND t.dept_name = #{deptName}


	AND t.dept_code = #{deptCode}


	AND t.parent_id = #{parentId}


	AND t.id != #{notEqualId}

SELECT max(t.tree_sort) + 120
FROM sys_dept t
WHERE t.del_flag = #{DEL_FLAG_NORMAL}

	and t.parent_id = #{parentId}

mapper.update:
update sys_dept
dept_code = #{deptCode}, dept_name = #{deptName}, dept_full_name = #{deptFullName}, dept_type = #{deptType}, address = #{address}, zip_code = #{zipCode}, dept_pinyin = #{deptPinyin}, leader = #{leader}, phone = #{phone}, email = #{email}, parent_id = #{parentId}, parent_ids = #{parentIds}, leader = #{leader}, tree_sort = #{treeSort}, tree_sorts = #{treeSorts}, tree_level = #{treeLevel}, tree_leaf = #{treeLeaf}, status = #{status}, update_by = #{updateBy}, update_time = #{updateTime}, update_ip = #{updateIp}, remark = #{remark}, version = version + 1
where id = #{id} and version = #{version}

update.foreach:
update sys_dept
dept_code = #{item.deptCode}, dept_name = #{item.deptName}, dept_full_name = #{item.deptFullName}, dept_type = #{item.deptType}, address = #{item.address}, zip_code = #{item.zipCode}, dept_pinyin = #{item.deptPinyin}, leader = #{item.leader}, phone = #{item.phone}, email = #{item.email}, parent_id = #{item.parentId}, parent_ids = #{item.parentIds}, leader = #{item.leader}, tree_sort = #{item.treeSort}, tree_sorts = #{item.treeSorts}, tree_level = #{item.treeLevel}, tree_leaf = #{item.treeLeaf}, status = #{item.status}, update_by = #{item.updateBy}, update_time = #{item.updateTime}, update_ip = #{item.updateIp}, remark = #{item.remark}, version = version + 1
where id = #{item.id} and version = #{item.version}

mapper.delete:
UPDATE sys_dept SET
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

delete.foreach:
UPDATE sys_dept SET
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{item.id}

mapper.select:
select d.id from sys_dept d left join sys_role_dept rd on d.id = rd.dept_id where rd.role_id = #{roleId}
and d.id not in (select d.parent_id from sys_dept d inner join sys_role_dept rd on d.id = rd.dept_id and rd.role_id = #{roleId})
order by d.parent_id, d.tree_sorts

mapper.update:
update sys_dept set parent_ids =

	when #{item.id} then #{item.parentIds}

, tree_sorts =

	when #{item.id} then #{item.treeSorts}

, tree_level =

	when #{item.id} then #{item.treeLevel}

where id in

	#{item.id}

mapper.select:
	select
	*
	from (

	WHERE t.del_flag = #{DEL_FLAG_NORMAL}

		and t.parent_id = #{parentId}

	UNION ALL

	WHERE t.del_flag = #{DEL_FLAG_NORMAL}

		and t.dept_id = #{parentId}

	) t
   order by groupSort,treeSorts

select
*
from (

WHERE t.del_flag = 0
UNION ALL

WHERE t.del_flag = 0
) t


		or t.id in

			#{id}



order by groupSort,treeSorts

