mapper.select:
SELECT

FROM sys_menu t
where id = #{id}

SELECT

FROM sys_menu t
where t.del_flag = #{DEL_FLAG_NORMAL}

SELECT max(t.tree_sort)
FROM sys_menu t
WHERE t.del_flag = #{DEL_FLAG_NORMAL}

	and t.parent_id = #{parentId}

SELECT
t.id
FROM sys_menu t
WHERE t.del_flag = '0'

	AND t.menu_name = #{menuName}


	AND t.menu_code = #{menuCode}


	AND t.path = #{path}


	AND t.parent_id = #{parentId}


	AND t.id != #{notEqualId}

select
from sys_menu t
and t.del_flag = '0' and ( 1 != 1
or t.id in
#{id}
)

mapper.update:
update sys_menu
menu_name = #{menuName}, menu_code = #{menuCode}, path = #{path}, component = #{component}, is_frame = #{isFrame}, is_cache = #{isCache}, menu_type = #{menuType}, visible = #{visible}, perms = #{perms}, icon = #{icon}, parent_id = #{parentId}, parent_ids = #{parentIds}, tree_sort = #{treeSort}, tree_sorts = #{treeSorts}, tree_level = #{treeLevel}, tree_leaf = #{treeLeaf}, status = #{status}, update_by = #{updateBy}, update_time = #{updateTime}, update_ip = #{updateIp}, remark = #{remark}, version = version + 1
where id = #{id} and version = #{version}

update.foreach:
update sys_menu
menu_name = #{menuName}, menu_code = #{menuCode}, path = #{path}, component = #{component}, is_frame = #{isFrame}, is_cache = #{isCache}, menu_type = #{menuType}, visible = #{visible}, perms = #{perms}, icon = #{icon}, parent_id = #{parentId}, parent_ids = #{parentIds}, tree_sort = #{treeSort}, tree_sorts = #{treeSorts}, tree_level = #{treeLevel}, tree_leaf = #{treeLeaf}, status = #{status}, update_by = #{updateBy}, update_time = #{updateTime}, update_ip = #{updateIp}, remark = #{remark}, version = version + 1
where id = #{item.id} and version = #{item.version}

mapper.update:
update sys_menu set parent_ids =

	when #{item.id} then #{item.parentIds}

, tree_sorts =

	when #{item.id} then #{item.treeSorts}

, tree_level =

	when #{item.id} then #{item.treeLevel}

where id in

	#{item.id}

mapper.delete:
UPDATE sys_menu SET
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

delete.foreach:
UPDATE sys_menu SET
del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{item.id}
