mapper.select:
SELECT

FROM sys_portlet a

WHERE a.id = #{id}

SELECT
    a.id
FROM sys_portlet a


    a.del_flag = #{DEL_FLAG_NORMAL}

        AND id != #{notEqualId}


        and code = #{code}

mapper.update:
UPDATE sys_portlet SET
    del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT name as "name", code as "code", id as "id", version as "version", m.role_id as "roleId", m.menu_id as "menuId" from sys_portlet a LEFT JOIN sys_role_menu m ON a.id = m.menu_id AND role_id = #{roleId} WHERE a.del_flag = #{DEL_FLAG_NORMAL}
ORDER BY ${page.orderByColumn}
ORDER BY a.sort

SELECT

from sys_portlet a
LEFT JOIN sys_role_menu m ON a.id = m.menu_id
WHERE a.del_flag = #{DEL_FLAG_NORMAL}
AND role_id = #{roleId}


        ORDER BY ${page.orderByColumn}


        ORDER BY a.sort
