mapper.select:
SELECT

FROM sys_theme_config a

WHERE a.id = #{id}

SELECT

FROM sys_theme_config a


    a.del_flag = #{DEL_FLAG_NORMAL}

        AND a.user_id = #{userId}


        and a.theme_obj like concat('%', #{themeObj}, '%')


        AND a.sort = #{sort}


        AND a.status = #{status}




        ORDER BY ${page.orderByColumn}


        ORDER BY a.sort

SELECT
    a.id
FROM sys_theme_config a


    a.del_flag = #{DEL_FLAG_NORMAL}

        AND id != #{notEqualId}

mapper.update:
UPDATE sys_theme_config SET
    del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
