mapper.select:
select
,
d.dept_name AS "sysDept.deptName", d.leader  AS "sysDept.leader"
from sys_user t
left join sys_dept d on t.dept_id = d.id
where t.del_flag = #{DEL_FLAG_NORMAL}


${params.dataScope}


		ORDER BY  ${page.orderBy}


		ORDER BY t.update_time DESC

SELECT
1
FROM sys_user t
where t.del_flag = #{DEL_FLAG_NORMAL}

	AND t.user_name = #{userName}


	AND t.name = #{name}


	AND t.no = #{no}


	AND t.email = #{email}


	AND t.phonenumber = #{phonenumber}


	AND t.id != #{notEqualId}

mapper.update:
update sys_user
dept_id = #{deptId}, name = #{name}, name_en = #{nameEn}, no = #{no}, user_name = #{userName}, nick_name = #{nickName}, user_type = #{userType}, email = #{email}, phonenumber = #{phonenumber}, sex = #{sex}, avatar = #{avatar}, password = #{password}, birthday = #{birthday}, nation = #{nation}, birth_address = #{birthAddress}, polity = #{polity}, title = #{title}, office_tel = #{officeTel}, fax = #{fax}, work_space = #{workSpace}, sort = #{sort}, user_pinyin = #{userPinyin}, login_ip = #{loginIp}, login_date = #{loginDate}, status = #{status}, update_by = #{updateBy}, update_time = #{updateTime}, update_ip = #{updateIp}, remark = #{remark}, version = version + 1
where id = #{id} and version = #{version}

 		update sys_user set
			login_ip = #{loginIp},
			login_date = #{loginDate}
 		where id = #{id}

mapper.delete:
UPDATE sys_user SET
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

UPDATE sys_user SET
del_flag = #{DEL_FLAG_DELETE}
WHERE id in

	#{ids}

delete.foreach:
UPDATE sys_user SET
del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{item.id}

mapper.select:
SELECT t.id, concat(d.parent_ids,'/',t.id) as 'parentIds', t.name, d.dept_name as 'subtitle', 'user' as 'icon' FROM sys_user t left join sys_dept d on t.dept_id = d.id
t.del_flag = 0 and ( 1 != 1
or t.id in
#{id}
)
